startColor = "#0066CC";
endColor = "#99cc00";
stepIn = 12;
stepOut = 12;
autoFade = true; 
sloppyClass = false; 
hexa = new makearray(16);
for (var i=0; i<10; i++)
	hexa[i] = i;
hexa[10] = "a";
hexa[11] = "b";
hexa[12] = "c";
hexa[13] = "d";
hexa[14] = "e";
hexa[15] = "f";
document.onmouseover = domouseover;
document.onmouseout = domouseout;
startColor = dehexize(startColor.toLowerCase());
endColor = dehexize(endColor.toLowerCase());
var fadeId = new Array();
function dehexize(Color) {
	var colorArr = new makearray(3);
	for (i=1; i<7; i++)
		for (j=0; j<16; j++)
			if (Color.charAt(i) == hexa[j])
				if (i%2 != 0)
					colorArr[Math.floor((i-1)/2)] = eval(j)*16;
				else
					colorArr[Math.floor((i-1)/2)] += eval(j);
	return colorArr;
}
function domouseover() {
	if (document.all) {
		var srcElement = event.srcElement;
		if ((srcElement.tagName == "A" && autoFade) || srcElement.className == "fade" || (sloppyClass && srcElement.className.indexOf("fade") != -1))
			fade(startColor, endColor, srcElement.uniqueID, stepIn); 
	}
}
function domouseout() {
	if (document.all) {
		var srcElement = event.srcElement;
		if ((srcElement.tagName == "A" && autoFade) || srcElement.className == "fade" || (sloppyClass && srcElement.className.indexOf("fade") != -1))
			fade(endColor, startColor, srcElement.uniqueID, stepOut);
	}
}
function makearray(n) {
	this.length = n;
	for (var i = 1; i <= n; i++)
		this[i] = 0;
	return this;
}
function hex(i) {
	if (i<0)
		return "00";
	else if (i>255)
		return "ff";
	else
		return "" + hexa[Math.floor(i/16)] + hexa[i%16];
}
function setColor(r, g, b, element) {
	var hr = hex(r);
	var hg = hex(g);
	var hb = hex(b);
	element.style.color = "#"+hr+hg+hb;
}
function fade(s, e, element, step) {
	var sr = s[0];
	var sg = s[1];
	var sb = s[2];
	var er = e[0];
	var eg = e[1];
	var eb = e[2];
	if (fadeId[0] != null && fade[0] != element) {
		setColor(sr, sg, sb, eval(fadeId[0]));
		var i = 1;
		while (i<fadeId.length) {
			clearTimeout(fadeId[i]);
			i++;
		}
	}
	for (var i=0; i<=step; i++)
		fadeId[i+1] = setTimeout("setColor(Math.floor(" +sr+ " *(( " +step+ " - " +i+ " )/ " +step+ " ) + " +er+ " * (" +i+ "/" +step+ ")), Math.floor(" +sg+ " * (( " +step+ " - " +i+ " )/ " +step+ " ) + " +eg+ " * (" +i+ "/" +step+")),Math.floor(" +sb+ " * ((" +step+ "-" +i+ ")/" +step+ ") + " +eb+ " * (" +i+ "/" +step+ ")),"+element+");", i*step);
	fadeId[0] = element;
}
function Rate(maxRatings) {
	this.maxRatings = maxRatings;
	this.over = function(id, score) {
		for (i=1; i<=score; i++)
			document.getElementById("rating-"+id+"-"+i).className = "over";
	}
	this.out = function(id, average) {
		for (i=1; i<=this.maxRatings; i++)
			document.getElementById("rating-"+id+"-"+i).className = this.style(i, average);
	}
	this.submit = function(id, score) {
		if (cookie.enabled()) {
			request("do=ratePost&score="+score+"&id="+id, "post-ratings-"+id);
			cookie.set("post-rated-"+id, score);
		} else
			alert("لطفا Cookies را در مرورگر خود فعال نماييد");
	}
	this.style = function(score, average) {
		return score <= average || score-average<.25 ? "on" : score-average<.75 ? "half" : "off";
	}
	this.ratings = function(id, votes) {
		var count = 0, average = 0, ratings = "";
		for (i=0; i<this.maxRatings; i++) {
			if (!votes[i])
				votes[i] = 0;
			count += votes[i];
			average += (i+1)*votes[i];
		}
		average /= count ? count : 1;
		for (i=1; i<=this.maxRatings; i++)
			ratings += "<li id=rating-"+id+"-"+i+(!cookie.get("post-rated-"+id) ? ' onmouseover="rate.over('+id+', '+i+')" onclick="rate.submit('+id+', '+i+')" onmouseout="rate.out('+id+', '+average+')" style="cursor: pointer"' : "")+' class='+this.style(i, average)+' title="'+i+' امتياز ('+votes[i-1]+' راي از '+count+' راي)"></li>';
		document.write("<p><span id=post-ratings-"+id+"><ul class=ratings>"+ratings+"</ul></span></p>");
	}
}
function copy(id) {
	document.getElementById(id).select();
	document.getElementById(id).focus();
	textRange = document.getElementById(id).createTextRange();
	textRange.execCommand("RemoveFormat");
	textRange.execCommand("Copy");
}
document.onselectstart = new Function("return false");
document.oncontextmenu = new Function("return false");
var rate = new Rate(configuration(post->max ratings));

function CPJS() {
	return <<<JS
function ControlPanel(l) {
	this.cache = new Array();
	this.cfg = {tab : "CPM", subMenu : "CPSM", sidebar : "CPS", content : "CPD", result : "result"};
	this.tab = function() {
		var c = "", i;
		for (i=0; i<this.cache["menu"].length; i++) {
			if (!document.getElementById("CPPD"+i)) {
				var getBodyTag = document.getElementsByTagName("body")[0];
				setdiv = document.createElement("div");
				setdiv.id = "CPPD"+i;
				setdiv.style.display = "none";
				getBodyTag.appendChild(setdiv);
			}
			c += '<div id="CPM'+i+'" class="bc_tm_item" onclick="return CP.onClick('+i+')" title="'+this.cache["menu"][i][0][2]+'"><img src="images/themes/silver/control panel/'+this.cache["menu"][i][0][3]+'.gif" />'+this.cache["menu"][i][0][0]+'</div>';
		}
		toggle(4, this.cfg.tab, c);
	}
	this.onClick = function(i) {
		this.cache["CPPD"+this.cache.selectedTab] = toggle(4, this.cfg.content);
		this.cache.selectedTab = i;
		if(typeof this.cache["CPPD"+i] == "undefined")
			this.click(this.cache["menu"][i][0][1], i);
		return this.menu(this.cache["menu"][i][1], i);
	}
	this.menu = function(m, i) {
		var c = "", j, n;
		this.tab();
		toggle(3, this.cfg.subMenu, "");
		toggle(4, this.cfg.result, "");
		toggle(3, this.cfg.sidebar, "");
		toggle(3, this.cfg.content, "");
		toggle(2, "CPM"+i, "bc_tm_item_sel");
		toggle(4, this.cfg.content, this.cache["CPPD"+i]);
		for (j=0; j<m.length; j++) {
			c += '<div class="bc_side_head"><img src="images/themes/silver/control panel/'+m[j][2]+'.gif" />'+m[j][0]+'</div><div class="bc_side_item">';
			for (n=0; n<m[j][1].length; n++)
				c += ' <span class="arrow">» </span> <a href="'+m[j][1][n][1]+'" onclick="return CP.click(\''+escape(m[j][1][n][1])+'\', '+i+', this)" title="'+m[j][1][n][2]+'">'+m[j][1][n][0]+'</a><br>';
			c += "</div>";
		}
		toggle(4, this.cfg.subMenu, c);
	}
	this.click = function(u, i, s) {
		toggle(3, this.cfg.sidebar, "");
		toggle(3, this.cfg.content, "");
		if (typeof s != "undefined") {
				for (var l=document.getElementById("CPSM").getElementsByTagName("a"), j=0; j<l.length; j++)
					l[j].style.color = "#3a3031";
			s.style.color = "#b70000";
		}
		d = unescape(u).split("javascript:");
		d[1] != null ? eval(d[1]) : load(d[0], this.cfg.content);
		return false;
	}
	this.item = function(i, c) {
		return "<div class=us_wlist id="+i+"><div class=us_wlist_in>"+c+"</div></div>";
	}
	this.cache["menu"] = l;
}
if (document.all || document.layers) {
	self.moveTo(0, 0);
	self.resizeTo(screen.availWidth, screen.availHeight);
}
JS;
}

