function Cookie(e, p, d, s) {
	this.expires = e;
	this.path = p;
	this.domain = d;
	this.secure = s;
	this.set = function(n, v, e) {
		if (!e)
			e = this.expires;
		document.cookie = n+"="+escape(v)+(e ? ";expires="+new Date(new Date().getTime()+e*1000).toGMTString() : "")+(this.path ? ";path="+this.path : "")+(this.domain ? ";domain="+this.domain : "")+(this.secure ? ";secure"+this.secure : "");
		return this.get(n) ? true : false;
	}
	this.get = function(n) {
		return new RegExp("(?:; )?"+n+"=([^;]*);?").test(document.cookie) ? unescape(RegExp["$1"]) : null;
	}
	this.unset = function(n) {
		if (this.get(n))
			document.cookie = n+"=;expires=Thu, 01-Jan-70 00:00:00 GMT"+(this.path ? ";path="+this.path : "")+(this.domain ? ";domain="+this.domain : "");
	}
	this.enabled = function() {
		return document.all ? navigator.cookieEnabled : this.set("temp", "temp");
	}
}
function toggle(m, o, v) {
	var t = typeof v != "undefined", e = window.document.getElementById(o), r = true;
	e || typeof o == "object" ? m == 1 ? e.disabled = t ? v : e.disabled == "on" ? "" : "on" : m == 2 ? t ? e.className = v : r = e.className : m == 3 ? e.style.display = t ? v : e.style.display == "none" ? "" : "none" : m == 4 ? eval("T=e.tagName.toLowerCase();T=T=='input'||T=='select'||T=='option'||T=='textarea';t?T?e.value=v:e.innerHTML=v:r=T?e.value:e.innerHTML") : m == 5 ? eval("for(var i=0;i<o.elements.length;i++){var e=o.elements[i];if(e.type=='checkbox'&&!e.disabled)e.checked=v;}") : 0 : r = 0;
	return r;
}
