	data += '</table><div align=center><input type=button class=button onclick="if(linkRow>28)this.disabled=true;insertRow(\'links\', linkElement, \'new_[name]\'+linkRow);linkRow++;return false" value="درج رديف"> <input type=submit class=button value="بروزرساني"></div></form>';
	toggle(4, "CPD", data);
