function AJAXE() {
	this.request = false;
	this.method = "post";
	this.url = "index.php";
	this.query = "";
	this.cache = false;
	this.headers = new Array();
	this.node = "";
	this.nodeValue = "";
	this.runLoading = true;
	this.msg = {0 : "شروع نشده ...", 1 : "در حال ارسال اطلاعات ...", 2 : "در حال بررسي شدن اطلاعات ...", 3 : "در حال دريافت اطلاعات ...", 404 : "پيدا نشدن پرونده ...", 500 : "خطاي داخلي در سرور ...", unknow : "عمل قبلي شما کامل اجرا نشد، لطفا اتصال خود را چک کنيد و دوباره تلاش کنيد ...", support : "!مرورگر شما ايژاکس را پشتيباني نمي کند", offline : ".ارتباط شما با سرور قطع است", bug : "!ايژاکس به اشکال بر خورده است"};
	this.failed = false;
	this.init = function() {
		var XmlHttpReq, msxmlhttp = new Array("Msxml2.XMLHTTP.5.0", "Msxml2.XMLHTTP.4.0", "Msxml2.XMLHTTP.3.0", "Msxml2.XMLHTTP", "Microsoft.XMLHTTP");
		if (typeof window.ActiveXObject != "undefined")
			for (var i=0; i<msxmlhttp.length; i++)
				try {
					XmlHttpReq = new ActiveXObject(msxmlhttp[i]);
				} catch (e) {
					XmlHttpReq = null;
				}
		else if (typeof window.XMLHttpRequest != "undefined") {
			XmlHttpReq = new XMLHttpRequest();
			if (XmlHttpReq.overrideMimeType)
				XmlHttpReq.overrideMimeType("text/xml");
		} else
			this.failed = true;
		this.request = XmlHttpReq;
	}
	this.queries = function(a) {
		var q = "";
		for (var k in a)
			q += k+"="+encodeURIComponent(a[k])+"&";
		this.query = q.slice(0, q.length-1);
	}
	this.setHeader = function(n, v) {
		this.headers.push(new Array(n, v));
	}
	this.sendHeaders = function() {
		for (var i=0; i<this.headers.length; i++)
			this.request.setRequestHeader(this.headers[i][0], this.headers[i][1]);
	}
	this.onUninitialized = function() {
		this.nodeValue = this.msg[0];
	}
	this.onLoading = function() {
		this.nodeValue = this.msg[1];
	}
	this.onLoaded = function() {
		this.nodeValue = this.msg[2];
	}
	this.onInteractive = function() {
		this.nodeValue = this.msg[3];
	}
	this.onCompletion = function() {
		this.nodeValue = this.getScript(this.response["Text"]);
	}
	this.onError = function() {
		switch (this.request.status) {
			case 404 :
				this.nodeValue = this.msg[404];
				break;
			case 500 :
				this.nodeValue = this.msg[500];
			default :
				this.nodeValue = this.msg["unknow"];
		}
	}
	this.onFail = function() {
		alert(this.msg["support"]);
	}
	this.run = function() {
		var self = this, rnd = (this.url.indexOf("?") == -1 ? "?" : "&")+(this.cache ? "" : "rnd="+new Date().getTime()+"&");
		if (this.failed) {
			this.onFail();
			return false;
		}
		if (!window.navigator.onLine) {
			alert(this.msg["offline"]);
			return false;
		}
		if (this.method.toLowerCase() == "get")
			this.request.open("GET", this.url+rnd+this.query, true);
		else {
			this.request.open("POST", this.url+rnd, true);
			this.setHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
		}
		if (!this.cache)
			this.setHeader("Cache-Control", "no-cache");
		this.sendHeaders();
		if (this.runLoading)
			self.loading(1);
		this.request.onreadystatechange = function(async) {
			switch (self.request.readyState) {
				case 0 :
					self.onUninitialized();
					break;
				case 1:
					self.onLoading();
					break;
				case 2:
					self.onLoaded();
					break;
				case 3:
					self.onInteractive();
					break;
				case 4:
					if (self.runLoading)
						self.loading(0);
					self.response = {Text : self.request.responseText, XML : self.request.responseXML};
					self.responseStatus = new Array(Math.floor(self.request.status), self.request.statusText);
					self.responseStatus[0] == 200 ? self.onCompletion() : self.onError();
					delete self.request;
			}
			toggle(4, self.node, self.nodeValue);
		}
		this.request.send(this.method.toLowerCase() == "get" ? null : this.query);
	}
	this.abort = function() {
		this.request.abort();
	}
	this.loading = function(s) {
		LoadingOnStatus(0);
		LoadingOnStatus(s);
	}
	this.getScript = function(s) {
		s = s.split("#JS");
		if (s[1] != null && s[0] != null) {
			setJS = document.createElement("script");
			setJS.setAttribute("type", "text/javascript");
			document.getElementsByTagName("head")[0].appendChild(setJS);
			setTimeout(setJS.text = s[0], 100);
			//setJS.text = "try{eval(s[0]);}catch(e){alert(e.fileName+' : '+e.lineNumber+'\\n'+e.name+' : '+e.message+'\\r\\n'+e.stack);}s[0]='';";
		}
		return s[1] != null ? s[1] : s[0];
	}
	this.init();
}
function LoadingOnStatus(Stats) {
	if (!Stats) {
		try {
			clearTimeout(TimeStatusLoading);
		} catch (e) {;}
		window.status = "Done";
		toggle(3, "loading", "none");
		return;
	}
	Stats = !(Stats%5) ? 1 : Stats%5;
	window.status = "Loading "+(Stats == 1 ? "-" : Stats == 2 ? "\\\" : Stats == 3 ? "|" : Stats == 4 ? "/" : "...");
	Stats++;
	toggle(3, "loading", "");
	TimeStatusLoading = setTimeout("LoadingOnStatus("+Stats+")", 250);
}
function sendXMLHttpRequest(q, m, n, c, p) {
	try {
		var r = new AJAXE();
		r.method = m;
		r.url = typeof p != "undefined" && p != null && p != "" ? p : "index.php";
		r.query = q;
		r.cache = typeof c != "undefined" && c != "" ? c : true;
		r.node = n;
		r.run();
	} catch (e) {
		alert("Unable to get XML data:\\n"+(typeof e == "string" ? e : e.message ? "["+e.name+"] "+e.message : "Unknown Error"));
	}
	return false;
}
function request(q, n, c, p) {
	return sendXMLHttpRequest(q, "post", n, c, p);
}
function ADI(q, n, c, m) {
	if (confirm(c))
		sendXMLHttpRequest(q, m ? m : "POST", n, false);
	return false;
}
function SAJAXF(o) {
	var i=0, q = "";
	for (; i<o.length; i++) {
		var T = o[i], n = T.name ? T.name : T.id, t = T.type, v = t == "checkbox" ? T.checked ? 1 : 0 : t == "radio" ? T.checked ? T.value : null : T.value
		if (typeof n != "undefined" && n != "" && v != null && !T.disabled)
			q += "&"+n+"="+encodeURIComponent(v);
	}
	return sendXMLHttpRequest(q.slice(1), o.getAttribute("method"), o.getAttribute("ajax") ? o.getAttribute("ajax") : "result", false, o.getAttribute("action"));
}
