function insertRow(Table, Elements, name, l) {
	var data = '', table = document.getElementById(Table), lastRow = table.rows.length;
	if (typeof l != "undefined")
		lastRow += l;
	var row = table.insertRow(lastRow);
	for (i=0; i<Elements.length; i++) {
		switch (Elements[i][0]) {
			case "text":
				el = document.createElement('input');
				el.setAttribute('type', 'txt');
				el.setAttribute('size', Elements[i][2]);
				el.setAttribute('className', 'input');
				break;
			case "checkbox":
				el = document.createElement('input');
				el.setAttribute('type', 'checkbox');
				el.setAttribute('className', 'checkbox');
				break;
			case "select":
				el = document.createElement('select');
				for (j=0; j<Elements[i][2].length; j++) {
					el.options[j] = new Option(Elements[i][2][j][0], Elements[i][2][j][1]);
					if (typeof(Elements[i][2][j][2]) != 'undefined')
						for (n=0; n<Elements[i][2][j][2].length; n++)
							el.options[j].setAttribute(Elements[i][2][j][2][n][0], Elements[i][2][j][2][n][0]);
				}
				el.setAttribute('className', 'select');
				break;
			case "textarea":
				el = document.createElement('textarea');
				el.setAttribute('cols', Elements[i][2][0]);
				el.setAttribute('rows', ((Elements[i][2][1] == 'auto') ? 1 : Elements[i][2][1]));
				if (Elements[i][2][1] == 'auto') {
					el.setAttribute('onblur', 'this.rows=1');
					el.setAttribute('onfocus', 'this.rows=3');
					el.style.overflow = 'hidden';
				}
				el.setAttribute('className', 'textarea');
				break;
			default:
				el.innerHTML = eval(Elements[i]);
		}
		parameters = (Elements[i][0] == 'text' || Elements[i][0] == 'checkbox') ? Elements[i][2] : Elements[i][3];
		if (typeof(parameters) != 'undefined')
			for (var parameter in parameters)
				el.setAttribute(parameter, parameters[parameter]);
		el.setAttribute('name', name.replace('[name]', Elements[i][1]));
		row.insertCell(i).appendChild(el);
	}
	return false;
}
function TOBJ(c) {
	document.getElementById("CPO").innerHTML = c;
	return document.getElementById("CPO");
}
function Confirm(m, t, a) {
	ht = document.getElementsByTagName("html");
	ht[0].style.filter = "progid:DXImageTransform.Microsoft.BasicImage(grayscale=1)";
	r = confirm(m) ? true : false;
	ht[0].style.filter = "";
	if (r && typeof t != "undefined")
		switch (t) {
			case "location" :
				document.location.href = a;
				break;
			default:
				eval(a);
		}
	return r;
}
function load(q, n, c, p) {
	return sendXMLHttpRequest(q, "get", n ? n : CP.cfg.content, c, p);
}
