function privateMessage() {
	this.data = new Array();
	this.compose = function(m, i, T, t, s, c) {
		toggle(4, i == "" ? CP.cfg.content : i, '<form method=post ajax'+(i != '' ? "="+i : "")+'><input type=hidden name=do value=PM /><input type=hidden name=action value='+(m != -1 && m != 3 ? 'send' : 'update')+' /><input type=hidden name=id value='+i+' />'+(m > 0 ? '<input type=hidden name=type value='+m+' /><input type=hidden name=to value='+T+' />' : '')+'<table border=0 cellspacing=0 cellpadding=3><tr><td>به:</td><td><table brder=0><tr><td><input name='+(m != 1 ? "to" : '""')+' dir=ltr class=input size=20 value="'+t+'" maxlength=30'+(m > 0 ? ' readonly' : '')+' /></td><td>'+(m < 2 ? " <input type=checkbox name=draft id=draft /><label for=draft>ذخيره در پيش نويس</labe>" : "")+'</td></tr></table></td></tr><tr><td>عنوان:</td><td><input class=input name=subject size=70 lang=fa value="'+s+'" maxlength=120'+(m > 0 ? ' readonly' : '')+' /></td></tr><tr><td>پيام:</td><td><textarea class=input rows=10 cols=70 name=content '+(configuration(message->html) == 1 ? "editor=active" : "lang=fa")+'>'+c+'</textarea></td></tr><tr align=center><td colspan=2><input type=submit class=input value="'+(m == 0 ? "ارسال" : m != -1 || m == 2 ? "پاسخ" : "ويرايش")+'" /></td></tr></table></form>');
