	this.item = function(t, l) {
		var i, c = "";
		if (t == "inbox" || t == "save" || t == "trash")
			for (i=0; i<l.length; i++)
				c += CP.item(l[i][0], (l[i][1] == 0 ? "<span class=arrow>» </span>" : "")+l[i][7]+' - مؤلف '+(l[i][3][0] != 0 ? '<a href="profile-'+l[i][3][0]+'.html" target=_blank>'+l[i][3][1]+'</a>' : '<a href="'+(l[i][11] != "" ? l[i][11] : "mailto:"+l[i][10])+'"'+(l[i][11] != "" ? ' title="'+l[i][10]+'"' : "")+' target=_blank>'+l[i][9]+'</a>')+' - '+l[i][5]+' در '+l[i][6]+' - [<a href="#" onclick="return PM.show('+l[i][0]+', \''+t+'\', '+l[i][1]+')">نمايش</a>]'+(t == "inbox" ? '[<a href="#" onclick="return PM.remove('+l[i][0]+', 2)">ذخيره</a>]' : t == "trash" ? '[<a href="#" onclick="return PM.remove('+l[i][0]+', 1)">بازگرداني</a>]' : "")+'[<a href="#" onclick="return PM.remove('+l[i][0]+', 3, \''+t+'\')">حذف</a>]');
		else if (t == "sent" || t == "outbox")
			for (i=0; i<l.length; i++)
				c += CP.item(l[i][0], l[i][7]+' - به <a href="profile-'+l[i][4][0]+'.html" target=_blank>'+l[i][4][1]+'</a> - '+l[i][5]+' در '+l[i][6]+' - [<a href="#" onclick="return PM.show('+l[i][0]+', \''+t+'\')">نمايش</a>]'+(t == "sent" ? '[<a href="#" onclick="return PM.remove('+l[i][0]+', 0)">ارسال مجدد</a>]' : '')+'[<a href="#" onclick="return PM.del('+l[i][0]+')">حذف</a>]');
		else
			for (i=0; i<l.length; i++)
				c += CP.item(l[i][0], l[i][7]+' - به <a href="profile-'+l[i][4][0]+'.html" target=_blank>'+l[i][4][1]+'</a> - '+l[i][5]+' در '+l[i][6]+' - [<a href="#" onclick="return PM.show('+l[i][0]+', \'draft\')">ويرايش</a>][<a href="#" onclick="return PM.del('+l[i][0]+')">حذف</a>]');
		return c;
	}
	this.show = function(i, t, o) {
		var s = this, r = new AJAXE(), d = this.data, k;
		r.query = "do=PM&action=show&id="+i+"&source="+(t == "draft" || t == "outbox" ? 1 : 0)+"&old="+(typeof o == "undefined" ? 1 : o);
		r.onCompletion = function() {
			for (k=0; k<d.length; k++)
				if (d[k][0] == i)
					break;
			d[k][8] = r.response["Text"];
			t == "draft" ? s.compose(-1, i, d[k][4][0], d[k][4][1], d[k][7], d[k][8]) : toggle(4, i, '<div style="text-align: right">'+(t == "inbox" || t == "save" || t == "trash" ? 'شما در تاريخ <span style="color: #0000FF">'+d[k][5]+'</span> در <span style="color: #0000FF">'+d[k][6]+'</span> پيامي دريافت نموده ايد، که به شرح زير مي باشد.<br />[<a href=# onclick="return PM.compose('+(PM.data[k][3][0] == 0 ? 2 : 1)+', '+i+', '+(PM.data[k][3][0] == 0 ? 'PM.data['+k+'][10]' : 'PM.data['+k+'][3][0]')+', '+(PM.data[k][3][0] == 0 ? 'PM.data['+k+'][10]' : 'PM.data['+k+'][3][1]')+', PM.data['+k+'][7], \'\')">پاسخ</a>]'+(t == "inbox" ? '[<a href=# onclick="return PM.remove('+i+', 2)">ذخيره</a>]' : t == "trash" ? '[<a href=# onclick="return PM.remove('+i+', 1)">بازگرداني</a>]' : "")+'[<a href=# onclick="return PM.remove('+i+', 3, \''+t+'\')">حذف</a>]<table border=0 cellspacing=5 cellpadding=5 width=90% dir=rtl><tr><td height=24>موضوع:</td><td height=24>'+d[k][7]+'</a></td></tr><tr><td width=20% height=30>مؤلف:</td><td width=80%><a href="'+(d[k][11] != "" ? d[k][11]+'" title="'+d[k][10]+'"' : "mailto:"+d[k][10])+'">'+d[k][9]+'</a></td></tr><tr><td height=30>پيام:</td><td>'+d[k][8]+'</td></tr><tr></tr></table>' : 'شما در تاريخ <span style="color: #0000FF">'+d[k][5]+'</span> در <span style="color: #0000FF">'+d[k][6]+'</span> پيامي ارسال نموده ايد، که به شرح زير مي باشد.<br />'+(t != "outbox" ? '[<a href=# onclick="return PM.remove('+i+', 0)">ارسال مجدد</a>]' : '[<a href=# onclick="return PM.compose(3, '+i+', PM.data['+k+'][4][0], PM.data['+k+'][4][1], PM.data['+k+'][7], PM.data['+k+'][8])">ويرايش</a>]')+'[<a href=# onclick="return PM.del('+i+')">حذف</a>]<table border=0 cellspacing=5 cellpadding=5 width=90% dir=rtl><tr><td height=24>موضوع:</td><td height="24">'+d[k][7]+'</a></td></tr><tr><td width=20% height=30>به:</td><td width=80%>'+d[k][4][1]+'</td></tr></tr><tr><td height=30>پيام:</td><td>'+d[k][8]+'</td></tr><tr></tr></table>')+'</div>');
		}
		r.run();
	}
	this.remove = function(i, t, s) {
		return s == "trash" ? this.del(i) : sendXMLHttpRequest("do=PM&action=remove&id="+i+"&type="+t, "post", i);
	}
	this.del = function(i) {
		var k, d = this.data;
		for (k=0; k<d.length; k++)
			if (d[k][0] == i)
				break;
		if (confirm("آيا مطمئن از حذف پيام "+d[k][7]+" هستيد؟"))
			return sendXMLHttpRequest("do=PM&action=delete&id="+i, "post", i);
	}
}
var PM = new privateMessage();
JS;
	}
	return $code;
}

/*
Name: فارسي نويس
URL: https://raha.group
Description: پشتيباني زبان فارسي براي فرم‌هاي وب
Date: 2007-01-01 00:00:00
Version: 1.0
Author: مهدي نظارتي زاده
Author URL: https://raha.group
