$database_name = $_POST["dataBaseName"];
$table_prefix = $_POST["tablePrefix"];
if (isset($_POST["install"]) && ($DB = new database($database_host, $database_username, $database_password, $database_name, $table_prefix)) && $_POST["password"] == $_POST["repassword"]) {
	foreach ($tree["data"]["sql"]["query"] as $q)
		$DB->create($q["value"], 1);
	foreach ($tree["data"]["theme"]["item"] as $item)
		$Theme[$item["attributes"]["name"]] = $DB->escape($item["value"]);
	foreach ($tree["data"]["templates"]["item"] as $item)
		$Template[$item["attributes"]["name"]] = $item["value"];
	foreach ($tree["data"]["plugins"]["item"] as $item)
		$DB->insert($DB->plugin, array("source" => $item["value"], "type" => $item[attributes][type], "status" => $item["attributes"]["status"]));
	$DB->insert($DB->post, array("title" => $tree["data"]["post"]["attributes"]["title"], "content" => $tree["data"]["post"]["value"], "author" => 1, "date" => Now));
	config("site", array("title" => $_POST["title"], "name" => $_POST["name"], "url" => str_replace("\\", "/", $_POST["url"]), "server path" => $_POST["path"], "description" => $_POST["description"], "keywords" => "", "author" => $_POST["username"], "email" => $_POST["email"], "create date" => date("Y-m-d H:i:s"), "date format" => "l j F Y", "time format" => "g:i A", "time zone" => "+3.5", "theme" => 1, "main page" => "main", "cache" => 0, "charset" => "UTF-8", "excerpt length" => 150));
	config("post", array("html" => 1, "max ratings" => 5, "order" => "date", "sort" => "desc", "ping status" => 1, "ping service" => "http://rpc.pingomatic.com\nhttp://rpc.weblogs.com\nhttp://ping.blo.gs\nhttp://rpc.technorati.com\nhttp://audiorpc.weblogs.com\nhttp://pinger.blogflux.com", "per page" => 3, "per feed" => 10, "show most last posts" => 10, "archive" => "monthly", "template" => $Template["download post"], "author order" => "id", "author sort" => "asc"));
	config("comment", array("order" => "time", "sort" => "asc", "html" => 0, "status" => 0, "per feed" => 0));
	config("category", array("default" => 1, "order" => "id", "sort" => "asc", "default icon" => "main.gif", "icon path" => "images/categories"));
	config("user", array("membership status" => 0, "membership subject" => "فعال کردن عضويت در [site title]", "membership message" => $Template["membership message"], "membership level" => 3, "avatar dimensions" => "128*152", "avatar size" => 102400, "avatar path" => "images/avatars", "signature length" => 300, "signature html" => 0, "timeout" => 300));
	foreach ($tree["data"]["avatars"]["item"] as $item)
		$avatar[$item["attributes"]["name"]] = $item["attributes"]["url"];
	config("avatar", $avatar);
	config("message", array("size" => 300, "html" => 0));
	config("newsletter", array("emails" => "", "status" => 1, "subject" => "بروز شدن سايت [site title] ([post title])", "template" => $Template["newsletter"]));
	config("guestbook", array("order" => "time", "sort" => "asc", "html" => 0, "status" => 0));
	config("link", array("order" => "time", "sort" => "asc", "per page" => 50, "per feed" => 0, "status" => 0, "theme" => $Template["linkbox"], "template" => $Template["link script"]));
	config("security code", array("background color" => "#D9FFC9", "border color" => "#FFFFFF", "foreground color" => "#666666", "line color" => "#8CBEFF", "line number" => 4, "length" => 5, "template" => $Template["security code"]));
	config("logo", array("background color" => "#898E79", "border color" => "#666666", "foreground color" => "#FFFFFF", "icon content" => $_POST["name"][0], "icon background color" => "#31659C", "icon foreground color" => "#FFFFFF", "line color" => "#FFFFFF", "site name" => $_POST["name"], "template" => $Template["logo"]));
	config("announcement", array("title" => "", "content" => "", "view" => "", "count" => "", "expires" => ""));
	config("bad word", array());
	config("banned ip", array("ip" => array(), "expires" => array(), "infraction" => array()));
	config("counter", array("today" => 0, "yesterday" => 0, "total" => 0, "update" => 0, "max visit" => "0 0", "max online" => "0 0", "online" => array(), "referrer" => "", "browser" => array("internet explorer" => 0, "fire fox" => 0, "opera" => 0, "net scape" => 0, "gecko" => 0, "other" => 0)));
	$emoticon["convert"] = 1;
	$emoticon["path"] = "http://us.i1.yimg.com/us.yimg.com/i/mesg/emoticons7/";
	foreach ($tree["data"]["smiles"]["item"] as $item) {
		$emoticon["data"]["code"][] = isset($item["attributes"]["code"]) ? $item["attributes"]["code"] : $item["value"];
		$emoticon["data"]["url"][] = $item["attributes"]["url"];
		$emoticon["data"]["description"][] = $item["attributes"]["description"];
		$emoticon["data"]["state"][] = $item["attributes"]["state"];
	}
	config("emoticon", $emoticon);
	config("tag", array("'<code([^>]*?)>(.*?)</code>'ise" => '\'<div class="code_header">کد:</div><div class="code_body"\'.stripslashes("\1").\'><pre>\'.htmlspecialchars(stripslashes("\2")).\'</pre></div>\'', "'<script[^>]*?>(.*?)</script>'is" => ""));
	$level["name"][1] = "مدير";
	$level["access"][1] = array("backup", "site state", "configuration", "emoticon", "announcement", "bad word", "banned ip", "tag", "counter", "link", "import link", "poll", "guestbook", "category", "newsletter", "theme", "plugin", "user", "profile", "user avatar", "level", "comment", "writer", "editor", "import post", "message");
	$level["name"][2] = "نويسنده";
	$level["access"][2] = array("writer", "profile", "comment", "message");
	$level["name"][3] = "عضو";
	$level["access"][3] = array("profile");
	$level["name"][4] = "مردم";
	$level["access"][4] = array("backup", "site state", "emoticon", "announcement", "bad word", "banned ip", "tag", "counter", "link", "import link", "poll", "guestbook", "category", "user", "profile", "user avatar", "level", "comment", "writer", "editor", "import post", "message", "demo");
	$level["increment"] = 4;
	config("level", $level);
	config("template", array("message" => $Template["message"], "coming soon" => $Template["coming soon"], "banned ip" => $Template["banned ip"]));
	config("cookie", array("expires" => 31536000, "path" => "/", "domain" => "", "secure" => 0));
	config("smtp", array("status" => 0, "host" => "", "username" => "", "password" => ""));
	$DB->insert($DB->user, array("username" => $_POST["username"], "password" => md5($_POST["password"]), "email" => $_POST["email"], "registered" => Time));
	mysql_query("insert into $DB->theme values ('', '$Theme[main]', '$Theme[comment]', '$Theme[post]', '$Theme[contact]', '$Theme[guestbook]', '{$Theme['link registration']}', '$Theme[profile]', '$Theme[membership]', '$Theme[emoticons]', '$Theme[login]', '{$Theme['control panel']}', '$Theme[style]', '$Theme[script]', '{$Theme['php script']}');");
	$_SESSION = array("logedIn" => 1, "username" => $_POST["username"], "password" => md5($_POST["password"]));
	$h = fopen(".htaccess", "w");
	fputs($h, str_replace("[PHP SELF]", $_SERVER["PHP_SELF"], "\n$Template[htaccess]"));
	fclose($h);
	$h = fopen("configuration.php", "w");
	fwrite($h, strtr($Template["configuration"], array("[host]" => $database_host, "[username]" => $database_username, "[password]" => $database_password, "[name]" => $database_name, "[prefix]" => $table_prefix)));
	fclose($h);
	chmod("configuration.php", 0666);
	unlink("data.xml");
} else {
	$schema = isset($_SERVER["HTTPS"]) && strtolower($_SERVER["HTTPS"]) == "on" ? "https://" : "http://";
	$port = $_SERVER["SERVER_PORT"] == 80 ? "" : ":$_SERVER[SERVER_PORT]";
	die("<script>".$tree["data"]["plugins"]["item"][1]["value"]."</script>".str_replace(array("[url]", "[path]"), array(dirname($schema.$_SERVER["SERVER_NAME"].$port.$_SERVER["PHP_SELF"]), str_replace("\\", "/", getcwd())), $tree["data"]["templates"]["install"]["value"]));
}
